import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import GameCard from "../components/GameCard";
import { getPlayedGames } from "../utils/gameService";

export default function Played() {
  const [playedGames, setPlayedGames] = useState([]);
  const [loading, setLoading] = useState(true);
  const [userId, setUserId] = useState(null);
  const navigate = useNavigate();

  const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000";

  useEffect(() => {
    const loadPlayedGames = async () => {
      try {
        const token = localStorage.getItem("steam_token");

        if (!token) {
          navigate("/login");
          return;
        }

        // Obtener info del usuario
        const userRes = await fetch(`${API_URL}/api/user`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!userRes.ok) {
          localStorage.removeItem("steam_token");
          navigate("/login");
          return;
        }

        const user = await userRes.json();
        setUserId(user.id);

        // Obtener juegos jugados desde Firebase
        const games = await getPlayedGames(user.id);
        console.log("üëÅÔ∏è Juegos jugados:", games.length);
        setPlayedGames(games);
        setLoading(false);
      } catch (err) {
        console.error("‚ùå Error cargando juegos jugados:", err);
        setLoading(false);
      }
    };

    loadPlayedGames();
  }, [API_URL, navigate]);

  if (loading) {
    return (
      <div className="page" style={{ textAlign: "center", marginTop: "100px" }}>
        <h1 className="page-title">Cargando juegos jugados...</h1>
      </div>
    );
  }

  if (playedGames.length === 0) {
    return (
      <div className="login-screen">
        <h1 className="page-title" style={{ textAlign: "center" }}>
          üëÅÔ∏è Juegos Jugados
        </h1>
        <p style={{ 
          textAlign: "center", 
          color: "#aaa", 
          marginTop: "20px",
          fontSize: "16px"
        }}>
          A√∫n no has marcado ning√∫n juego como jugado.
        </p>
        <p style={{ 
          textAlign: "center", 
          color: "#aaa", 
          marginTop: "10px",
          fontSize: "14px"
        }}>
          Ve a tu biblioteca y marca los juegos que ya jugaste üéÆ
        </p>
      </div>
    );
  }

  return (
    <div className="login-screen">
      <h1 className="page-title" style={{ textAlign: "center" }}>
        üëÅÔ∏è Juegos Jugados ({playedGames.length})
      </h1>
      <p style={{ 
        textAlign: "center", 
        color: "#aaa", 
        marginBottom: "30px",
        fontSize: "14px"
      }}>
        Juegos que has marcado como jugados
      </p>
      <div className="games-grid-vertical">
        {playedGames.map((game) => (
          <GameCard 
            key={game.appid} 
            game={game} 
            userId={userId}
          />
        ))}
      </div>
    </div>
  );
}
