import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const LoginScreen = () => {
  const navigate = useNavigate();
  const [checking, setChecking] = useState(true);
  const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000";

  useEffect(() => {
    // Verificar si ya hay un token v√°lido
    const checkExistingToken = async () => {
      const token = localStorage.getItem("steam_token");

      if (token) {
        console.log("üîç Token encontrado, verificando validez...");
        try {
          const res = await fetch(`${API_URL}/api/user`, {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          });

          if (res.ok) {
            console.log("‚úÖ Token v√°lido, redirigiendo a biblioteca...");
            navigate("/library");
            return;
          } else {
            console.log("‚ùå Token inv√°lido, eliminando...");
            localStorage.removeItem("steam_token");
          }
        } catch (err) {
          console.error("Error verificando token:", err);
          localStorage.removeItem("steam_token");
        }
      }

      setChecking(false);
    };

    checkExistingToken();
  }, [API_URL, navigate]);

  const handleSteamLogin = () => {
    console.log("üöÄ Iniciando login con Steam...");
    console.log("üîó URL:", `${API_URL}/auth/steam`);
    // Redirigir al login de Steam a trav√©s del backend
    window.location.href = `${API_URL}/auth/steam`;
  };

  if (checking) {
    return (
      <div className="login-screen">
        <h1 className="login-title">Verificando sesi√≥n...</h1>
      </div>
    );
  }

  return (
    <div className="login-screen">
      <h1 className="login-title">√önete ahora üë§</h1>
      <p style={{ color: '#aaa', marginBottom: '30px', fontSize: '16px' }}>
        Conecta tu cuenta de Steam para ver tu biblioteca
      </p>
      <button className="login-button" onClick={handleSteamLogin}>
        Iniciar sesi√≥n con Steam
      </button>

      {/* Bot√≥n de debug */}
      <div style={{ marginTop: '40px' }}>
        <button 
          onClick={() => {
            const token = localStorage.getItem("steam_token");
            console.log("üîç Token actual:", token ? "Existe" : "No existe");
            console.log("üìù Token completo:", token);
          }}
          style={{
            padding: '8px 16px',
            background: 'rgba(255,255,255,0.1)',
            border: '1px solid rgba(255,255,255,0.2)',
            borderRadius: '8px',
            color: '#aaa',
            cursor: 'pointer',
            fontSize: '12px'
          }}
        >
          üîç Verificar Token (Debug)
        </button>
      </div>
    </div>
  );
};

export default LoginScreen;
